mapper.select:
SELECT COUNT(*)
FROM TBL_BOARD
WHERE BOARD_TYPE = #{boardType}

mapper.update:
UPDATE TBL_BOARD
	SET BOARD_TITLE = #{boardTitle},
		BOARD_CONTENT = #{boardContent},
		BOARD_TYPE = #{boardType}
	WHERE BOARD_NO = #{boardNo}

UPDATE TBL_COMMENT
	SET COMMENT_CONTENT = #{commentContent}
	WHERE COMMENT_NO = #{commentNo}
